import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [videoUrl, setVideoUrl] = useState("");
  const [summary, setSummary] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [chatHistory, setChatHistory] = useState([]);

  const handleSummary = async () => {
    try {
      const res = await axios.post("http://localhost:8000/summary", {
        video_url: videoUrl,
      });
      setSummary(res.data.summary);
    } catch (error) {
      console.error("Error fetching summary:", error);
    }
  };

  const handleQuestion = async () => {
    try {
      const res = await axios.post("http://localhost:8000/question", {
        video_url: videoUrl,
        question,
        chat_history: chatHistory,
      });
      setAnswer(res.data.answer);
      setChatHistory(res.data.chat_history);
    } catch (error) {
      console.error("Error fetching answer:", error);
    }
  };

  return (
    <div className="app-container">
      <h2 className="title">ðŸŽ¥ YouTube Summarizer + Chatbot</h2>
      <input
        className="input-field"
        type="text"
        placeholder="Enter YouTube URL"
        value={videoUrl}
        onChange={(e) => setVideoUrl(e.target.value)}
      />
      <br />
      <button className="btn" onClick={handleSummary}>Generate Summary</button>

      <div>
        <h4>ðŸ“„ Summary:</h4>
        <textarea className="textarea" value={summary} readOnly rows={10}></textarea>
      </div>

      <hr />

      <input
        className="input-field"
        type="text"
        placeholder="Ask a question about the video"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
      />
      <br />
      <button className="btn" onClick={handleQuestion}>Ask</button>

      <div>
        <h4>ðŸ’¬ Answer:</h4>
        <textarea className="textarea" value={answer} readOnly rows={5}></textarea>
      </div>

      <div>
        <h4>ðŸ“œ Chat History:</h4>
        <textarea className="textarea" value={chatHistory.join("\n\n")} readOnly rows={10}></textarea>
      </div>
    </div>
  );
}

export default App;
