import React, { useState } from "react";
import axios from "axios";

function App() {
  const [videoUrl, setVideoUrl] = useState("");
  const [summary, setSummary] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [chatHistory, setChatHistory] = useState([]);

  const handleSummary = async () => {
    try {
      const res = await axios.post("http://localhost:8000/summary", {
        video_url: videoUrl,
      });
      setSummary(res.data.summary);
    } catch (error) {
      console.error("Error fetching summary:", error);
    }
  };

  const handleQuestion = async () => {
    try {
      const res = await axios.post("http://localhost:8000/question", {
        video_url: videoUrl,
        question,
        chat_history: chatHistory,
      });
      setAnswer(res.data.answer);
      setChatHistory(res.data.chat_history);
    } catch (error) {
      console.error("Error fetching answer:", error);
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>YouTube Summarizer + Chatbot</h2>
      <input
        style={{ width: "80%", marginBottom: "10px" }}
        type="text"
        placeholder="Enter YouTube URL"
        value={videoUrl}
        onChange={(e) => setVideoUrl(e.target.value)}
      />
      <br />
      <button onClick={handleSummary}>Generate Summary</button>
      <div>
        <h4>Summary:</h4>
        <textarea
          value={summary}
          readOnly
          rows={10}
          style={{ width: "100%" }}
        ></textarea>
      </div>
      <hr />
      <input
        style={{ width: "80%", marginBottom: "10px" }}
        type="text"
        placeholder="Ask a question about the video"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
      />
      <br />
      <button onClick={handleQuestion}>Ask</button>
      <div>
        <h4>Answer:</h4>
        <textarea
          value={answer}
          readOnly
          rows={5}
          style={{ width: "100%" }}
        ></textarea>
      </div>
      <div>
        <h4>Chat History:</h4>
        <textarea
          value={chatHistory.join("\n\n")}
          readOnly
          rows={10}
          style={{ width: "100%" }}
        ></textarea>
      </div>
    </div>
  );
}

export default App;
